import React, { Component } from "react";
import "./SelectOpt";
import "./Checkbox";
import { Container, Row, CardLink } from "reactstrap";
import tracker from "./api/tracker";
import AliceCarousel from 'react-alice-carousel';

class basket extends Component {
    constructor() {
        super();
        this.state = {
            products: [],

        };
    }
    componentDidMount = async () => {
        var basket = localStorage.getItem('basket');
        console.log("basket", basket);
        try {
            if (basket === null) {
                console.log("Empty basket");
            }
            else {
                const response = await tracker.get("basket/" + basket);
                const arrayproduct = response.data;
                console.log("res", response);
                this.setState({ products: arrayproduct });
            }
        } catch (e) {
            console.log(e);
        }
    }

    handle_add_one = async (id) => {
        var basket = localStorage.getItem('basket');
        console.log("handle_add_one icindeyim");
        try {
            const response = await tracker.put("basket/byOne/" + basket, {
                product: id
            });
            console.log("handle_add_one response", response);
        } catch (e) { }
    }
    handle_delete_one = async (id) => {
        var basket = localStorage.getItem('basket');
        console.log("handle_delete_one icindeyim");
        try {
            const response = await tracker.put("basket/delete/byOne/" + basket, {
                product: id
            });
            console.log("handle_add_one response ", response);
            const quantity = response.data.quantity;
            if (quantity === 0) {
                const del_response = await tracker.put("/basket/delete/" + basket, {
                    product: id
                });
                console.log("quantity=0 ", del_response);
            }
        } catch (e) { }
    }

    handleProducts = () => {
        console.log("handleproduct");
        var product = [];
        if (this.state.products.length !== 0) {
            console.log(this.state.products)
            var productitem;

            for (var i = 0; i < this.state.products.length; i++) {
                console.log(this.state.products[i])
                const id = this.state.products[i]._id;
                /*const image = this.state.products[i].image;*/
                const price = this.state.products[i].price;
                const name = this.state.products[i].name;
                const quantity = this.state.products[i].quantity;
                productitem = (
                    <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>

                        <AliceCarousel

                            disableButtonsControls={true} mouseTracking={false}
                            responsive autoPlay autoPlayInterval="3000" style={{ height: "40vh", marginBottom: "5vh" }}>

                        </AliceCarousel>
                        <h4>{name}</h4>
                        <p class="price">${price}</p>


                        <Row> <CardLink onClick={() => { this.handle_add_one(id) }}><button>+</button>
                        </CardLink>
                            <p class="quantity">{quantity}</p>
                        </Row> <CardLink onClick={() => { this.handle_delete_one(id) }}><button>-</button>
                        </CardLink>

                    </div >

                );
                product.push(productitem);
            }
            console.log(product);
            return product;
        }
    }
    render() {
        return (

            <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
                <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
                    <h1 className="mt-5">Basket</h1>
                    <Row style={{ margin: "5vh", justifyContent: "space-around" }}>{this.handleProducts()}</Row>
                </div>
            </Container>


        );
    }

}
export default basket;
