import React, { Component } from 'react';
import Login from './Login';
import axios from 'axios';
import './SelectOpt';
import './Checkbox';
import { Button, Container, Form, Input, Row } from 'reactstrap';
import { BrowserRouter as Switch, Route, Link } from 'react-router-dom';

import Indeterminate from './Checkbox';
import tracker from '../src/api/tracker';

class Reg extends Component {
    constructor() {
        super();
        this.state = {
            Email: '',
            Password: '',
            Gender: '',
            status:'',
            message:'',
        }

        this.Email = this.Email.bind(this);
        this.Password = this.Password.bind(this);
        this.Gender = this.Gender.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    Email(event) {
        this.setState({ Email: event.target.value })
    }

    Password(event) {
        this.setState({ Password: event.target.value })
    }

    Gender(event) {
        this.setState({ Gender: event.target.value })
    }

    handleSubmit = async () => {

        try {
            const response = await tracker.post('/register', {
                email: this.state.Email,
                password: this.state.Password,
                gender: this.state.Gender,
            });
            const token= response.data.token;
            const msg=  response.data.message;
            console.log("msg ",msg);
            console.log("token",token) ;
            this.setState({status:response.status, message:msg})
            console.log(response.status)
        } catch (e) {
            console.log(e.message);
        }
       
    }

    render() {


        return (

            < div className="form" >

                < Container className="form-container" >

                    < Row className="form" >
                        < Form>
                            < div class="row" className="mb-2 pageheading" >
                                < div className="login-check" > Sign Up</div >
                            </div >

                            < Input className="mb-4" type="text" onChange={this.Email} placeholder="Enter Email" />
                            < Input className="mb-4" type="password" onChange={this.Password} placeholder="Enter Password" />

                            <div className='Selectopt'>
                                <select value={this.state.Gender} onChange={this.Gender} className='Selectopt'>
                                    <option>Select gender</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                </select>
                            </div>

                            <div className='notify' for='cb'><Indeterminate /></div>

                            < Button className="b-login" onClick={this.handleSubmit} color="secondary" block > Create Account</Button >

                            <div className="login-check">Already registered? < Link to={'/Login'} className="nav-link2" pathname="Login!" > Login </Link >
                                < Switch >
                                    < Route path='/Login' component={Login} />
                                </Switch >
                            </div>
                        </Form >
                    </Row >
                </Container >
            </div >

        );

    }
}

export default Reg;
