import React, { Component } from "react";


import "./SelectOpt";
import "./Checkbox";
import Heels from "./images/heels.png";
import Topuklu from "./images/kadÄ±ntopuklu.jpeg";
import tracker from "../src/api/tracker";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";
import { CardLink, Container, Row } from "reactstrap";
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';


import topuklu1 from "./images/topuklu_1.jpg";
import topuklu1_2 from "./images/topuklu_1_2.jpg";
import topuklu1_3 from "./images/topuklu_1_3.jpg";
import topuklu2 from "./images/topuklu_2.jpg";
import topuklu2_2 from "./images/topuklu_2_2.jpg";
import topuklu2_3 from "./images/topuklu_2_3.jpg";
import topuklu3 from "./images/topuklu_3.jpg";
import topuklu3_2 from "./images/topuklu_3_2.jpg";
import topuklu3_3 from "./images/topuklu_3_3.jpg";
import topuklu4 from "./images/topuklu_4.jpg";
import topuklu4_2 from "./images/topuklu_4_2.jpg";
import topuklu4_3 from "./images/topuklu_4_3.jpg";
import topuklu5 from "./images/topuklu_5.jpg";
import topuklu5_2 from "./images/topuklu_5_2.jpg";
import topuklu5_3 from "./images/topuklu_5_3.jpg";
import topuklu6 from "./images/topuklu_6.jpg";
import topuklu6_2 from "./images/topuklu_6_2.jpg";
import topuklu6_3 from "./images/topuklu_6_3.jpg";
import topuklu7 from "./images/topuklu_7.jpg";
import topuklu7_2 from "./images/topuklu_7_2.jpg";
import topuklu7_3 from "./images/topuklu_7_3.jpg";
import topuklu8 from "./images/topuklu_8.jpg";
import topuklu8_2 from "./images/topuklu_8_2.jpg";
import topuklu8_3 from "./images/topuklu_8_3.jpg";
import topuklu9 from "./images/topuklu_9.jpg";
import topuklu9_2 from "./images/topuklu_9_2.jpg";
import topuklu9_3 from "./images/topuklu_9_3.jpg";
import topuklu10 from "./images/topuklu_10.jpg";
import topuklu10_2 from "./images/topuklu_10_2.jpg";
import topuklu10_3 from "./images/topuklu_10_3.jpg";
var listOfImages;
const im ="./images/topuklu_2.jpg";

class womenHeels extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: [Heels, Topuklu, Topuklu],
      image1:null,
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/female/category/Heels");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });

      console.log("state", this.state.products);
    } catch (e) {}
  };
  
  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i]);
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image[0];
        const price = this.state.products[i].price;
        const newprice = this.state.products[i].newprice;
        const name = this.state.products[i].name;
        const image1 = this.state.products[i].image[0];
        console.log("image", image);
        const imagearray = [
          [topuklu1, topuklu1_2, topuklu1_3],
          [topuklu2, topuklu2_2, topuklu2_3],
          [topuklu3, topuklu3_2, topuklu3_3],
          [topuklu4, topuklu4_2, topuklu4_3],
          [topuklu5, topuklu5_2, topuklu5_3],
          [topuklu6, topuklu6_2, topuklu6_3],
          [topuklu7, topuklu7_2, topuklu7_3],
          [topuklu8, topuklu8_2, topuklu8_3],
          [topuklu9, topuklu9_2, topuklu9_3],
          [topuklu10, topuklu10_2, topuklu10_3],
        ];
      
        const coni = i;
        productitem = (
            <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh", justifyContent: "space-around" }}>
            <CardLink
              onClick={() => {
                this.props.history.push("/product/" + id + '/wh/' + coni);
              }}
              style={{ color: "black" }}
            >
              <AliceCarousel
                disableButtonsControls={true}
                mouseTracking={false}
                responsive
                autoPlay
                autoPlayInterval="3000"
                style={{ height: "40vh", marginBottom: "5vh" }}
              >
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />
              </AliceCarousel>
                    <h5>{name}</h5>
                    <p class="price" style={{ marginBottom: "0px", textDecorationLine: "line-through" }}>${price}</p>
              <p class="price">${newprice}</p>
                    <button>Add to Cart</button>
            </CardLink>
     
         
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };
  handleOrder = async (e,price,val) =>{
    console.log("orderr")
    try {
      const response = await tracker.get("product/gender/male/category/Boots/"+price+"/"+val);
      const arrayproduct = response.data;
      console.log("resOrder", response);
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) { }
  }
  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Woman Heels</h1>
            <Navbar collapseOnSelect expand="lg" bg="light" variant="light" {...this.props}>
              <NavDropdown title="Order by" id="collasible-nav-dropdown">
                <NavDropdown.Item onSelect={(e) => this.handleOrder(e,"price","1")}>Price (Ascending)</NavDropdown.Item>
                <NavDropdown.Item onSelect={(e) => this.handleOrder(e,"price","-1")}>Price (Descending)</NavDropdown.Item>
                <NavDropdown.Item onSelect={(e) => this.handleOrder(e,"rate","1")}>Rate (Ascending)</NavDropdown.Item>
                <NavDropdown.Item onSelect={(e) => this.handleOrder("price","-1")}>Rate (Descending)</NavDropdown.Item>
              </NavDropdown>
            </Navbar>
                    <Row style={{ margin: "5vh", justifyContent: "space-around"  }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default womenHeels;
