import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Reg from './register';
import { Button, Container, Form, Input, Row } from 'reactstrap';
import tracker from '../src/api/tracker';


import { BrowserRouter as Switch, Route, Link } from 'react-router-dom';



class pm_add_product extends Component {
    constructor() {
        super();
        this.state = {
            productManager: "",
            id:"",
            name: "",
            brand: "",
            category: "",
            description: "",
            price: 0,
            
            //seller: { type: mongoose.Schema.Types.ObjectID, ref: 'user' },
            countInStock:0,
            size: 0,
            gender: "",

        }
    }
    handleSubmit = async () => {
        try {
            const response = await tracker.put("/product" + this.state.id, {
                productManager: "", //product manager id al�nacak

                name: this.state.name,
                brand: this.state.brand,
                category: this.state.category,
                description: this.state.description,
                price: this.state.price,
                rate: this.state.rate,

                countInStock: this.state.countInStock,
                size: this.state.size, //array olmal�
                gender: this.state.gender,


            })

        } catch (e) { console.log(e); }
      


    }
    render() {
        return (
            <>
            <div style={{ marginLeft: "-125px", paddingBottom: "2rem" }}>
                <ProSidebar >

                    <SidebarContent>
                        <Menu iconShape="square" icon={<BiMenu />} >
                            <MenuItem icon={<BiCommentEdit />}>Comments<Link to="/product_manager_comments" /></MenuItem>
                            <SubMenu title="Edit Products" icon={<BiShoppingBag />}>
                                <MenuItem >Add Product<Link to="/pm_add_product" /></MenuItem>
                                <MenuItem>Delete Product</MenuItem>
                                <MenuItem>Edit Product</MenuItem>
                            </SubMenu>
                        </Menu>
                    </SidebarContent>


                </ProSidebar>
            </div>
            < div className="form" >

                < Container className="form-container" >

                    < Row className="form" >
                        < Form >
                            < div className="login-check" >Add New Product</div >

                            < Input className="mb-4" type="text" onChange={this.id} placeholder="Enter product id" />
                            < Input className="mb-4" type="text" onChange={this.name} placeholder="Enter product name" />
                            < Input className="mb-4" type="text" onChange={this.brand} placeholder="Enter brand" />
                            < Input className="mb-4" type="text" onChange={this.category} placeholder="Enter category" />
                            < Input className="mb-4" type="text" onChange={this.description} placeholder="Enter description" />
                            < Input className="mb-4" type="number" onChange={this.price} placeholder="Enter price" />
                            < Input className="mb-4" type="number" onChange={this.countInStock} placeholder="Enter count in stock" />

                            < Input className="mb-4" type="number" onChange={this.size} placeholder="Enter size" />

                            <div className='Selectopt'>
                                <select value={this.state.Gender} onChange={this.gender} className='Selectopt'>
                                    <option>Select gender</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                </select>
                            </div>

                            < Button className="b-login" onClick={this.handleSubmit} color="secondary" block >Add</Button >

                           

                        </Form >

                    </Row >

                </Container >

            </div >
            </>
            
            );
    }
}
export default pm_add_product;
