import React, { Component } from "react";
import { Container, Row, Button, CardLink } from "reactstrap";
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Sonnet from 'react-bootstrap/Sonnet';
import Cards from 'react-credit-cards';
import ReactStars from "react-rating-stars-component";
import tracker from '../src/api/tracker';
import { render } from "react-dom";
class Product_details extends Component {
    constructor() {
        super();
        this.state = {
            product:[]
        };
    }
    handleBasket = async (id, price) => {
        var basket = localStorage.getItem('basket');
        console.log("basket", basket);
        if (basket === null) {
            console.log("ifdeyim")
            try {
                const response = await tracker.post("/basket", {
                    productList: {
                        totalprice: "",
                        productArray: [
                            {
                                quantity: 1,
                                price: price,
                                product: id
                            }
                        ]
                    }
                });
                console.log(response);
                const basketid = response.data._id;
                localStorage.setItem('basket', basketid);


            } catch (e) {
                console.log(e);
            }
        }
        else {
            try {
                const basketresponse = await tracker.get("basket/" + basket);
                const obj = { quantity: 1, price: price, product: id };
                console.log("basket response: ", basketresponse);
                var includes = false;
                const array = basketresponse.data.productList.productArray;
                console.log("array: ", array);
                for (var i = 0; i < array.length; i++) {
                    console.log("array[i].product: ", array[i].product);
                    if (array[i].product === id) {
                        includes = true;
                        break;
                    }
                    else
                        includes = false;
                }

                if (includes === false) {
                    const response = await tracker.put("basket/" + basket, obj);
                    console.log("baskette ayn� �r�n yok, response: ", response);
                }
                else if (includes === true) {

                    console.log("baskette ayn� �r�n var 1 ekle");

                    const response = await tracker.put("basket/byOne/" + basket, {
                        product: id
                    });
                    console.log("add_one response", response);

                }

            } catch (e) {
                console.log(e);
            }
        }

    }

    render() {
        const { data } = this.props.location;
        const ratingChanged = (newRating) => {
            console.log(newRating);
        };
        this.state.product = { data };
        return (
            <Tabs defaultActiveKey="Description" id="uncontrolled-tab-example">
                <Tab eventKey="Description" title="Home">
                    <Sonnet>
                        <div className="card" style={{ width: "100vh", height: "200vh", margin: "8vh" }}>
                        <CardLink
                            style={{ color: "black" }}
                        >

                            <h4>{this.state.product.name}</h4>
                            <p class="price">${this.state.product.price}</p>
                        </CardLink>
                        <CardLink style={{ marginLeft: "0px" }}
                            onClick={() => {
                                this.handleBasket(this.state.product._id, this.state.product.price);
                            }}
                        >
                            <button>Add to Cart</button>
                        </CardLink>
                    </div>
                   </Sonnet>
                    
                </Tab>
                <Tab eventKey="Reviews" title="Reviews">

                    <ReactStars
                        count={5}
                        onChange={ratingChanged}
                        size={24}
                        isHalf={true}
                        emptyIcon={<i className="far fa-star"></i>}
                        halfIcon={<i className="fa fa-star-half-alt"></i>}
                        fullIcon={<i className="fa fa-star"></i>}
                        activeColor="#ffd700"
                    />
                    <div>Reviews</div>

                </Tab>
               
            </Tabs>
            );
    }
}
export default Product_details;
