import React, { Component } from 'react';
import Icon from '@material-ui/core/Icon';
import DeleteForeverRoundedIcon from '@material-ui/icons/DeleteRounded';
import './App.css';
import { Button, Container, Form, Input, Row,Col, CardLink } from 'reactstrap';
import tracker from '../src/api/tracker';

class user_info extends Component {
    constructor() {

        super();


        this.state = {
            email: "",
            address: "",
           
        }
    }
    componentDidMount = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        try {
            const response = await tracker.get("address/" + loggedin);
            if (response.data !== null) {
                console.log("response.data not null, return address");
                this.setState({ address: response.data });
            }
        } catch (e) {
            console.log(e);
        }
    }
    componentDidUpdate = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        try {
            const response = await tracker.get("address/" + loggedin);
            if (response.data !== null) {
                console.log("response.data not null, return address");
                this.setState({ address: response.data });
            }
        } catch (e) {
            console.log(e);
        }
    }
    handleEmail = async () => {


    }
    handleDeleteAddress = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        const response = await tracker.delete('adress/' + loggedin);
        console.log("handleDeleteAddress: ", response);
        this.setState({ address: '' });
    }
    editAddress = () => {
        console.log("editAddress'teyim");
        var add = (
            <>
                < Input className="mb-2" type="textarea" onChange={this.address} placeholder="Enter Your New Address Here" />
                <div className="row" style={{ justifyContent: "center" }}>
                    < Button style={{ width: "auto" }} className="b-login"
                        onClick={this.handleEditAddress} color="secondary" block >Edit</Button >
                </div>
            </>
        );
        return add;
    }
    handleEditAddress = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        const response = await tracker.put('adress/' + loggedin, { adress: this.state.address });
        console.log("handleAddAddress: ", response);

    }

    handleaddAddress = async () => {
        const response = await tracker.post('adress/',{ adress: this.state.address });
        console.log("handleaddAddress: ", response);
    }
    addAddress = () => {
        console.log("addAddress'teyim");
        var add = (
            <>
            < Input className="mb-2" type="textarea" onChange={this.address} placeholder="Enter Your Address Here" />
            <div className="row" style={{ justifyContent: "center" }}>
                < Button style={{ width: "auto" }} className="b-login"
                    onClick={this.handleaddAddress} color="secondary" block >Add</Button >
                </div>
                </>
            );
        return add;
    }
    handleAddress = () => {
        var address_info;
        var addressarray = [];
        console.log("handleaddress'teyim");
        console.log(this.state.address);
        if (this.state.address === '') {
           
            address_info = (
                <div>
                    <Col>
                        <Row><label>Add an address!</label></Row>
                        <Row><Icon onClick={this.addAddress} style={{ fontSize: 30 }}/></Row>
                     
                    </Col>
                </div>

            );
            addressarray.push(address_info);
        }
        else {
            console.log("response.data not null, return address");
           
            address_info = (
                <div> <Col><Row><DeleteForeverRoundedIcon onClick={this.handleDeleteAddress} /><CardLink onClick={this.handleEditAddress}>Edit</CardLink></Row>
                    <Row>{this.state.address}</Row>
                </Col></div>
            );
            addressarray.push(address_info);
        }
        return addressarray;
    }
    render() {
        return (
            <>
                < div className="form" >

                    < Container className="form-container" >

                        < Row className="form" >
                            < Form >
                                <Row><label>Your email address: </label>{this.handleEmail}</Row>
                                <Row><label>Your delivery address:</label></Row>
                                <Row>{this.handleAddress}</Row>
                                
                                
                            </Form >

                        </Row >

                    </Container >

                </div >
                </>
            
            );
    }
}
export default user_info;
