import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";
// import { Button, Col, Container, Form, Input, Modal, Row } from "reactstrap";
// import erkekClassic from "./images/erkekklasik.jpeg";
// import tracker from "./api/tracker";
import { CardLink, Container, Row } from "reactstrap";
import erkekClassic from "./images/erkekklasik.jpeg";
import tracker from "./api/tracker";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";

import menClassic1 from "./images/menClassic_1.jpg";
import menClassic1_2 from "./images/menClassic_1_2.jpg";
import menClassic1_3 from "./images/menClassic_1_3.jpg";
import menClassic2 from "./images/menClassic_2.jpg";
import menClassic2_2 from "./images/menClassic_2_2.jpg";
import menClassic2_3 from "./images/menClassic_2_3.jpg";
import menClassic3 from "./images/menClassic_3.jpg";
import menClassic3_2 from "./images/menClassic_3_2.jpg";
import menClassic3_3 from "./images/menClassic_3_3.jpg";
import menClassic4 from "./images/menClassic_4.jpg";
import menClassic4_2 from "./images/menClassic_4_2.jpg";
import menClassic4_3 from "./images/menClassic_4_3.jpg";
import menClassic5 from "./images/menClassic_5.jpg";
import menClassic5_2 from "./images/menClassic_5_2.jpg";
import menClassic5_3 from "./images/menClassic_5_3.jpg";
import menClassic6 from "./images/menClassic_6_2.jpg";
import menClassic6_2 from "./images/menClassic_6_2.jpg";
import menClassic6_3 from "./images/menClassic_6_3.jpg";
import menClassic7 from "./images/menClassic_7.jpg";
import menClassic7_2 from "./images/menClassic_7_2.jpg";
import menClassic7_3 from "./images/menClassic_7_3.jpg";
import menClassic8 from "./images/menClassic_8.jpg";
import menClassic8_2 from "./images/menClassic_8_2.jpg";
import menClassic8_3 from "./images/menClassic_8_3.jpg";
import menClassic9 from "./images/menClassic_9.jpg";
import menClassic9_2 from "./images/menClassic_9_2.jpg";
import menClassic9_3 from "./images/menClassic_9_3.jpg";
import menClassic10 from "./images/menClassic_10.jpg";
import menClassic10_2 from "./images/menClassic_10_2.jpg";
import menClassic10_3 from "./images/menClassic_10_3.jpg";

class menClassic extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: [erkekClassic, erkekClassic],
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/male/category/Classics");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };
  handleBasket = async (id, price) => {
    var basket = localStorage.getItem("basket");
    console.log("basket", basket);
    if (basket === null) {
      console.log("ifdeyim");
      try {
        const response = await tracker.post("/basket", {
          productList: {
            totalprice: "",
            productArray: [
              {
                quantity: 1,
                price: price,
                product: id,
              },
            ],
          },
        });
        console.log(response);
        const basketid = response.data._id;
        localStorage.setItem("basket", basketid);
      } catch (e) {}
    } else {
      try {
        const response = await tracker.put("basket/" + basket, {
          quantity: 1,
          price: price,
          product: id,
        });
        console.log(response);
      } catch {}
    }
  };
  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i]);
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image;
        const price = this.state.products[i].price;
        const name = this.state.products[i].name;

        console.log("image", image);
        const imagearray = [
          [menClassic1, menClassic1_2, menClassic1_3],
          [menClassic2, menClassic2_2, menClassic2_3],
          [menClassic3, menClassic3_2, menClassic3_3],
          [menClassic4, menClassic4_2, menClassic4_3],
          [menClassic5, menClassic5_2, menClassic5_3],
          [menClassic6, menClassic6_2, menClassic6_3],
          [menClassic7, menClassic7_2, menClassic7_3],
          [menClassic8, menClassic8_2, menClassic8_3],
          [menClassic9, menClassic9_2, menClassic9_3],
          [menClassic10, menClassic10_2, menClassic10_3],
        ];
        const coni = i;
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <CardLink
              onClick={() => {
                this.props.history.push("/product/" + id + "/mc" + "/" + coni);
              }}
              style={{ color: "black" }}
            >
              <AliceCarousel
                disableButtonsControls={true}
                mouseTracking={false}
                responsive
                autoPlay
                autoPlayInterval="3000"
                style={{ height: "40vh", marginBottom: "5vh" }}
              >
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][0]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />
              </AliceCarousel>
              <h4>{name}</h4>
              <p class="price">${price}</p>
            </CardLink>
            <CardLink style={{marginLeft:"0px"}}
              onClick={() => {
                this.handleBasket(id, price);
              }}
            >
              <button>Add to Cart</button>
            </CardLink>
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Man Classics</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-between" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default menClassic;
