import React, { Component, createContext } from "react";
import "./SelectOpt";
import "./Checkbox";
import { CardLink, Container, Row } from "reactstrap";
import erkekClassic from "./images/erkekklasik.jpeg";
import tracker from "./api/tracker";
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";

import menBoot1 from './images/menBoots_1.jpg'
import menBoot1_2 from './images/menBoots_1_2.jpg'
import menBoot1_3 from './images/menBoots_1_3.jpg'
import menBoot2 from './images/menBoots_2.jpg'
import menBoot2_2 from './images/menBoots_2_2.jpg'
import menBoot2_3 from './images/menBoots_2_3.jpg'
import menBoot3 from './images/menBoots_3.jpg'
import menBoot3_2 from './images/menBoots_3_2.jpg'
import menBoot3_3 from './images/menBoots_3_3.jpg'
import menBoot4 from './images/menBoots_4.jpg'
import menBoot4_2 from './images/menBoots_4_2.jpg'
import menBoot4_3 from './images/menBoots_4_3.jpg'
import menBoot5 from './images/menBoots_5.jpg'
import menBoot5_2 from './images/menBoots_5_2.jpg'
import menBoot5_3 from './images/menBoots_5_3.jpg'
import menBoot6 from './images/menBoots_6.jpg'
import menBoot6_2 from './images/menBoots_6_2.jpg'
import menBoot6_3 from './images/menBoots_6_3.jpg'
import menBoot7 from './images/menBoots_7.jpg'
import menBoot7_2 from './images/menBoots_7_2.jpg'
import menBoot7_3 from './images/menBoots_7_3.jpg'
import menBoot8 from './images/menBoots_8.jpg'
import menBoot8_2 from './images/menBoots_8_2.jpg'
import menBoot8_3 from './images/menBoots_8_3.jpg'
import menBoot9 from './images/menBoots_9.jpg'
import menBoot9_2 from './images/menBoots_9_2.jpg'
import menBoot9_3 from './images/menBoots_9_3.jpg'
import menBoot10 from './images/menBoots_10.jpg'
import menBoot10_2 from './images/menBoots_10_2.jpg'
import menBoot10_3 from './images/menBoots_10_3.jpg'
import menBoot11 from './images/menBoots_11.jpg'
import menBoot11_2 from './images/menBoots_11_2.jpg'
import menBoot11_3 from './images/menBoots_11_3.jpg'
import menBoot12 from './images/menBoots_12.jpg'


class menBoots extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: [erkekClassic, erkekClassic],
    };

  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/male/category/Boots");
      const arrayproduct = response.data;
      console.log("res", response);
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {

    }
  };
  
 
  handleBasket = async (id, price) => {
    var basket = localStorage.getItem('basket');
    console.log("basket", basket);
    if (basket === null) {
      console.log("ifdeyim")
      try {
        const response = await tracker.post("/basket", {
          productList: {
            totalprice: "",
            productArray: [
              {
                quantity: 1,
                price: price,
                product: id
              }
            ]
          }
        });
        console.log(response);
        const basketid = response.data._id;
        localStorage.setItem('basket', basketid);


      } catch (e) {

      }
    }
    else {
      try {

        const response = await tracker.put("basket/" + basket, {

          quantity: 1,
          price: price,
          product: id

        });
        console.log(response);

      } catch {

      }
    }

  }

  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      console.log(this.state.products)
      var productitem;

      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i])
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image;
        const price = this.state.products[i].price;
        const name = this.state.products[i].name;

        console.log("image", image)
        const imagearray = [ [menBoot1,menBoot1_2, menBoot1_3],[menBoot2,menBoot2_2, menBoot2_3],[menBoot3,menBoot3_2, menBoot3_3],
          [menBoot4,menBoot4_2, menBoot4_3],[menBoot5,menBoot5_2, menBoot5_3],[menBoot6,menBoot6_2, menBoot6_3],
          [menBoot7,menBoot7_2, menBoot7_3],[menBoot8,menBoot8_2, menBoot8_3],[menBoot9,menBoot9_2, menBoot9_3],
          [menBoot10,menBoot10_2, menBoot10_3],[menBoot11,menBoot11_2, menBoot11_3],[menBoot12,menBoot12, menBoot12], ];
        

        console.log("imagearray", imagearray);
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            {/* <CardLink onClick={() => {
              this.props.history.push('/product/' + id)
            }} style={{ color: "black" }}> */}
                <AliceCarousel 
                
                        disableButtonsControls = {true} mouseTracking={false}
                        responsive autoPlay autoPlayInterval="3000" style={{ height: "40vh", marginBottom:"5vh"}}>
              <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][0]} alt="High Heels" />
              <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
              <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />

              </AliceCarousel>
              <h4>{name}</h4>
              <p class="price">${price}</p>
              

            {/* </CardLink> */}
            <CardLink onClick={() => { this.handleBasket(id, price) }}><button>Add to Cart</button>
            </CardLink>

          </div >

        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Man Boots</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-around" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default menBoots;
