import React, { Component } from "react";
import { Button, Container, CardLink, Input, Row, Col } from 'reactstrap';

import tracker from '../src/api/tracker';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';

class Checkout extends Component {
    constructor() {
        super();
        this.state = {
            Email: "",
            address: "",

            cvc: '',
            expiry: '',
            focus: '',
            name: '',
            number: '',
        };
    }
    componentDidMount = async () => {
        var mail = localStorage.getItem('email');
        var userId = localStorage.getItem('user');
        this.setState({ Email: mail });
        const response = await tracker.get("address/" + userId);
        if (response.data !== null) {
            this.setState({ address: response.data });
        }
    }
    handleInputFocus = (e) => {
        this.setState({ focus: e.target.name });
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;

        this.setState({ [name]: value });
    }

    handleAddress = () => {
       
    }
    handleEmail = () => {
    
    }
    handleAccount = () => {
        var loggedin = localStorage.getItem('email');
        console.log("loggedin: ", loggedin);
        var address;
        try {
            if (loggedin !== null && this.state.address!=="") {
                console.log("Logged in");
                address = (
                    <div style={{ padding: "2rem" }}>
                        < p >{this.state.Email}</p>
                        < p style={{
                            display: "flex",
                            textAlign:"center",
                            justifyContent:"center",
                            height: "25px",
                            width: "275px"}}>{this.state.address} </p>
                    </div>
                );
            }
            else if (loggedin !== null && this.state.address === "") {
                console.log("Logged in");
                address = (
                    <div style={{ padding: "2rem" }}>
                        < p style={{
                            display: "flex",
                            justifyContent: "center",
                            height: "25px",
                            width: "275px"
                        }}>{this.state.Email}</p>
                        < Input className="mb-4" type="text" onChange={this.state.address} placeholder="Enter Address" />
                    </div>
                );
            }
            else {
                address = (
                    <div style={{ padding: "2rem" }}>
                        < Input className="mb-4" type="text" onChange={this.state.Email} placeholder="Enter Email" />
                        < Input className="mb-4" type="text" onChange={this.state.address} placeholder="Enter Address" />
                    </div>
                );

            }
            return address;
        } catch (e) {
            console.log(e);
        }
    }



    render() {
        const { data } = this.props.location;
        return (
            < div className="form" >

                < Container className="form-container" >

                    <Tabs defaultActiveKey="Address" >
                        <Tab eventKey="Address" title="Address">
                            <div>Total Cost: ${data}</div>
                            {this.handleAccount()}
                            
                        </Tab>
                        <Tab eventKey="Payment" title="Payment">
                            <Container>
                                <div id="PaymentForm">
                                    <div>Total Cost: {data}</div>
                                    <Row style={{ padding: "2rem" }}>
                                        <Col>
                                            <Cards
                                                cvc={this.state.cvc}
                                                expiry={this.state.expiry}
                                                focused={this.state.focus}
                                                name={this.state.name}
                                                number={this.state.number}
                                            />
                                        </Col>
                                        <Col>
                                            <form>
                                                <input className="mb-4"
                                                    type="string"
                                                    name="name"
                                                    placeholder="Card Holder"
                                                    onChange={this.handleInputChange}
                                                    onFocus={this.handleInputFocus}
                                                />

                                                <input className="mb-4"
                                                    type="tel"
                                                    name="number"
                                                    placeholder="Card Number"
                                                    onChange={this.handleInputChange}
                                                    onFocus={this.handleInputFocus}
                                                />

                                                <input className="mb-4"
                                                    maxLength={4}
                                                    type="tel"
                                                    name="expiry"
                                                    placeholder="Expiry Date"
                                                    onChange={this.handleInputChange}
                                                    onFocus={this.handleInputFocus}
                                                />


                                                <input className="mb-4"
                                                    maxLength={3}
                                                    type="tel"
                                                    name="cvc"
                                                    placeholder="CVC"
                                                    onChange={this.handleInputChange}
                                                    onFocus={this.handleInputFocus}
                                                />




                                            </form></Col>
                                    </Row>
                                    <CardLink > <Button onClick="" >Proceed Payment</Button > </CardLink>
                                </div>
                            </Container>
                        </Tab>

                    </Tabs>

                </Container >

            </div >
        );
    }

}
export default Checkout;
