import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";
import { Button, Col, Container, Form, Input, Modal, Row, CardLink } from "reactstrap";
import ManSneaker from "./images/erkekspor.jpeg";
import tracker from "./api/tracker";

class menSneakers extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: [ManSneaker],
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/male/category/Sneakers");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };
  handleClick = () => {

  }
  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i])
        const id = this.state.products[i]._id;
        const image= this.state.products[i].image;
        console.log("image", image);
        productitem = (
          <CardLink onClick = { () => {
            this.props.history.push('/product/'+id)
          }} style={{color:"black"}}><div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <img style={{ width: "25vh", height: "25vh" }} src={this.state.images[i]} alt="High Heels" />
            <h4>{this.state.products[i].name}</h4>
            <p class="price">${this.state.products[i].price}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
          </CardLink>
        );
        product.push(productitem);
      }
      for (var i = 0; i < this.state.products.length; i++) {
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <img style={{ width: "25vh", height: "25vh" }} src={this.state.images[i]} alt="High Heels" />
            <h4>{this.state.products[i].name}</h4>
            <p class="price">${this.state.products[i].price}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
        );
        product.push(productitem);
      }
      for (var i = 0; i < this.state.products.length; i++) {
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <img style={{ width: "25vh", height: "25vh" }} src={this.state.images[i]} alt="High Heels" />
            <h4>{this.state.products[i].name}</h4>
            <p class="price">${this.state.products[i].price}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Man Sneakers</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-between" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default menSneakers;
