import React, { Component } from 'react';
import tracker from '../src/api/tracker'
import './App.css';
import Reg from './register';
import { Button, Container, Form, Input, Row } from 'reactstrap';
import { BrowserRouter as Switch, Route, Link } from 'react-router-dom';


const required = value => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};


class Login extends Component {

    constructor() {

        super();


        this.state = {
            Email: '',
            Password: '',

            status: '',
            message: '',

        }


        this.Password = this.Password.bind(this);

        this.Email = this.Email.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);


    }


    Email(event) {

        this.setState({ Email: event.target.value })

    }

    Password(event) {

        this.setState({ Password: event.target.value })

    }


     

    handleSubmit = async () => {

        try {
            this.form.validateAll();
            const response = await tracker.post('/login', {
                email: this.state.Email,
                password: this.state.Password,

            });
            const token = response.data.token;
            const msg = response.data.message;
            
            console.log("msg ", msg);
            console.log("token", token);
            this.setState({ status: response.status, message: msg })
            console.log(response.status)
            if (response.status === 200) {
                localStorage.setItem("user", JSON.stringify(token));
                this.props.history.push("/profile");
                window.location.reload();
            }
        } catch (e) {
            console.log(e.message);
        }

    }


    render() {


        return (

            < div className="form" >

                < Container className="form-container" >

                    < Row className="form" >


                        < Form ref={c => {
                            this.form = c;
                        }}>



                            < div className="login-check" >

                                Login
                                
                             </div >



                            


                            < Input className="mb-4" type="text" onChange={this.Email} placeholder="Enter Email" validations={[required]}/>


                            < Input className="mb-4" type="password" onChange={this.Password} placeholder="Enter Password" validations={[required]} />


                            < Button className="b-login" onClick={this.handleSubmit} color="secondary" block > Login</Button >

                            <div className="login-check"> You don't have an account, yet? < Link to={'/register'} className="nav-link2" pathname="Sign Up!" > Register </Link >
                                < Switch >

                                    < Route path='/register' component={Reg} />

                                </Switch >
                            </div>

                        </Form >

                    </Row >

                </Container >

            </div >

        );

    }

}


export default Login;
