import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";

import Boots from "./images/kadÄ±nbot.jpeg";
import { CardLink, Container, Row } from "reactstrap";
import tracker from "../src/api/tracker";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";

import womenBoot1 from "./images/womenBoots_1.jpg";
import womenBoot1_2 from "./images/womenBoots_1_2.jpg";
import womenBoot1_3 from "./images/womenBoots_1_3.jpg";
import womenBoot2 from "./images/womenBoots_2.jpg";
import womenBoot2_2 from "./images/womenBoots_2_2.jpg";
import womenBoot2_3 from "./images/womenBoots_2_3.jpg";
import womenBoot3 from "./images/womenBoots_3.jpg";
import womenBoot3_2 from "./images/womenBoots_3_2.jpg";
import womenBoot3_3 from "./images/womenBoots_3_3.jpg";
import womenBoot4 from "./images/womenBoots_4.jpg";
import womenBoot4_2 from "./images/womenBoots_4_2.jpg";
import womenBoot4_3 from "./images/womenBoots_4_3.jpg";
import womenBoot5 from "./images/womenBoots_5.jpg";
import womenBoot5_2 from "./images/womenBoots_5_2.jpg";
import womenBoot5_3 from "./images/womenBoots_5_3.jpg";
import womenBoot6 from "./images/womenBoots_6.jpg";
import womenBoot6_2 from "./images/womenBoots_6_2.jpg";
import womenBoot6_3 from "./images/womenBoots_6_3.jpg";
import womenBoot7 from "./images/womenBoots_7.jpg";
import womenBoot7_2 from "./images/womenBoots_7_2.jpg";
import womenBoot7_3 from "./images/womenBoots_7_3.jpg";
import womenBoot8 from "./images/womenBoots_8.jpg";
import womenBoot8_2 from "./images/womenBoots_8_2.jpg";
import womenBoot8_3 from "./images/womenBoots_8_3.jpg";
import womenBoot9 from "./images/womenBoots_9.jpg";
import womenBoot9_2 from "./images/womenBoots_9_2.jpg";
import womenBoot9_3 from "./images/womenBoots_9_3.jpg";
import womenBoot10 from "./images/womenBoots_10.jpg";
import womenBoot10_2 from "./images/womenBoots_10_2.jpg";
import womenBoot10_3 from "./images/womenBoots_10_3.jpg";
// import womenBoot11 from "./images/womenBoots_11.jpg";
// import womenBoot11_2 from "./images/womenBoots_11_2.jpg";
// import womenBoot11_3 from "./images/womenBoots_11_3.jpg";
// import womenBoot12 from "./images/womenBoots_12.jpg";

class womenBoots extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: Boots,
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/female/category/Boots");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };

  handleBasket = async (id, price) => {
    var basket = localStorage.getItem("basket");
    console.log("basket", basket);
    if (basket === null) {
      console.log("ifdeyim");
      try {
        const response = await tracker.post("/basket", {
          productList: {
            totalprice: "",
            productArray: [
              {
                quantity: 1,
                price: price,
                product: id,
              },
            ],
          },
        });
        console.log(response);
        const basketid = response.data._id;
        localStorage.setItem("basket", basketid);
      } catch (e) {}
    } else {
      try {
        const response = await tracker.put("basket/" + basket, {
          quantity: 1,
          price: price,
          product: id,
        });
        console.log(response);
      } catch {}
    }
  };

  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i]);
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image;
        const price = this.state.products[i].price;
        const name = this.state.products[i].name;

        console.log("image", image);
        const imagearray = [
          [womenBoot1, womenBoot1_2, womenBoot1_3],
          [womenBoot2, womenBoot2_2, womenBoot2_3],
          [womenBoot3, womenBoot3_2, womenBoot3_3],
          [womenBoot4, womenBoot4_2, womenBoot4_3],
          [womenBoot5, womenBoot5_2, womenBoot5_3],
          [womenBoot6, womenBoot6_2, womenBoot6_3],
          [womenBoot7, womenBoot7_2, womenBoot7_3],
          [womenBoot8, womenBoot8_2, womenBoot8_3],
          [womenBoot9, womenBoot9_2, womenBoot9_3],
          [womenBoot10, womenBoot10_2, womenBoot10_3],
          //   [womenBoot11, womenBoot11_2, womenBoot11_3],
          //   [womenBoot12, womenBoot12, womenBoot12],
        ];
        const coni = i;
        console.log("imagearray", imagearray);
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <CardLink
              onClick={() => {
                this.props.history.push("/product/" + id + "/wb" + "/" + coni);
              }}
              style={{ color: "black" }}
            >
              <AliceCarousel
                disableButtonsControls={true}
                mouseTracking={false}
                responsive
                autoPlay
                autoPlayInterval="3000"
                style={{ height: "40vh", marginBottom: "5vh" }}
              >
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][0]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />
              </AliceCarousel>
              <h4>{name}</h4>
              <p class="price">${price}</p>
            </CardLink>
            <CardLink style={{marginLeft:"0px"}}
              onClick={() => {
                this.handleBasket(id, price);
              }}
            >
              <button>Add to Cart</button>
            </CardLink>
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Woman Boots</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-around" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default womenBoots;
