import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";
import { Button, Col, Container, Form, Input, Modal, Row, CardLink } from "reactstrap";
import ManSneaker from "./images/erkekspor.jpeg";
import tracker from "./api/tracker";

import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";

import menSneakers1 from "./images/menSneakers_1.jpg";
import menSneakers1_2 from "./images/menSneakers_1_2.jpg";
import menSneakers1_3 from "./images/menSneakers_1_3.jpg";
import menSneakers2 from "./images/menSneakers_2.jpg";
import menSneakers2_2 from "./images/menSneakers_2_2.jpg";
import menSneakers2_3 from "./images/menSneakers_2_3.jpg";
import menSneakers3 from "./images/menSneakers_3.jpg";
import menSneakers3_2 from "./images/menSneakers_3_2.jpg";
import menSneakers3_3 from "./images/menSneakers_3_3.jpg";
import menSneakers4 from "./images/menSneakers_4.jpg";
import menSneakers4_2 from "./images/menSneakers_4_2.jpg";
import menSneakers4_3 from "./images/menSneakers_4_3.jpg";
import menSneakers5 from "./images/menSneakers_5.jpg";
import menSneakers5_2 from "./images/menSneakers_5_2.jpg";
import menSneakers5_3 from "./images/menSneakers_5_3.jpg";
import menSneakers6 from "./images/menSneakers_6.jpg";
import menSneakers6_2 from "./images/menSneakers_6_2.jpg";
import menSneakers6_3 from "./images/menSneakers_6_3.jpg";
import menSneakers7 from "./images/menSneakers_7.jpg";
import menSneakers7_2 from "./images/menSneakers_7_2.jpg";
import menSneakers7_3 from "./images/menSneakers_7_3.jpg";
import menSneakers8 from "./images/menSneakers_8.jpg";
import menSneakers8_2 from "./images/menSneakers_8_2.jpg";
import menSneakers8_3 from "./images/menSneakers_8_3.jpg";
import menSneakers9 from "./images/menSneakers_9.jpg";
import menSneakers9_2 from "./images/menSneakers_9_2.jpg";
import menSneakers9_3 from "./images/menSneakers_9_3.jpg";
import menSneakers10 from "./images/menSneakers_10.jpg";
import menSneakers10_2 from "./images/menSneakers_10_2.jpg";
import menSneakers10_3 from "./images/menSneakers_10_3.jpg";
// import menSneakers11 from "./images/menSneakers_11.jpg";
// import menSneakers11_2 from "./images/menSneakers_11_2.jpg";
// import menSneakers11_3 from "./images/menSneakers_11_3.jpg";
// import menSneakers12 from "./images/menSneakers_12.jpg";

class menSneakers extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: [ManSneaker],
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/male/category/Sneakers");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };
  handleClick = () => {};

  handleBasket = async (id, price) => {
    var basket = localStorage.getItem("basket");
    console.log("basket", basket);
    if (basket === null) {
      console.log("ifdeyim");
      try {
        const response = await tracker.post("/basket", {
          productList: {
            totalprice: "",
            productArray: [
              {
                quantity: 1,
                price: price,
                product: id,
              },
            ],
          },
        });
        console.log(response);
        const basketid = response.data._id;
        localStorage.setItem("basket", basketid);
      } catch (e) {}
    } else {
      try {
        const response = await tracker.put("basket/" + basket, {
          quantity: 1,
          price: price,
          product: id,
        });
        console.log(response);
      } catch {}
    }
  };

  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;

      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i]);
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image;
        const price = this.state.products[i].price;
        const name = this.state.products[i].name;

        const imagearray = [
          [menSneakers1, menSneakers1_2, menSneakers1_3],
          [menSneakers2, menSneakers2_2, menSneakers2_3],
          [menSneakers3, menSneakers3_2, menSneakers3_3],
          [menSneakers4, menSneakers4_2, menSneakers4_3],
          [menSneakers5, menSneakers5_2, menSneakers5_3],
          [menSneakers6, menSneakers6_2, menSneakers6_3],
          [menSneakers7, menSneakers7_2, menSneakers7_3],
          [menSneakers8, menSneakers8_2, menSneakers8_3],
          [menSneakers9, menSneakers9_2, menSneakers9_3],
          [menSneakers10, menSneakers10_2, menSneakers10_3],
          // [menSneakers11, menSneakers11_2, menSneakers11_3],
          // [menSneakers12, menSneakers12, menSneakers12],
        ];

        console.log("image", image);
        const coni = i;
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <CardLink
              onClick={() => {
                this.props.history.push("/product/" + id + "/ms" + "/" + coni);
              }}
              style={{ color: "black" }}
            >
              <AliceCarousel
                disableButtonsControls={true}
                mouseTracking={false}
                responsive
                autoPlay
                autoPlayInterval="3000"
                style={{ height: "40vh", marginBottom: "5vh" }}
              >
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][0]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />
              </AliceCarousel>
              <h4>{name}</h4>
              <p class="price">${price}</p>
            </CardLink>
            <CardLink style={{marginLeft:"0px"}}
              onClick={() => { 
                this.handleBasket(id, price);
              }}
            >
              <button>Add to Cart</button>
            </CardLink>
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Man Sneakers</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-between" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default menSneakers;
