import React, { Component } from 'react';

import './SelectOpt';
import './Checkbox';
import { Button, Col, Container, Form, Input, Modal, Row } from 'reactstrap';
import Boots from './images/kadınbot.jpeg'
import tracker from '../src/api/tracker'

class product extends Component {

    constructor() {
        super();
        this.state = {
            product:"",
        }



    }

    componentDidMount= async () =>{
        try{
            const id= this.props.match.params.id
            console.log(id);
            const response = await tracker.get("product/" + id);
            console.log(response);
            this.setState({product:response.data})
        }catch(e){

        }
    }
    render() {

        return (
            <>
                <Container style={{ width: "max-content", opacity: "", marginTop: "5vh", textAlign: "-webkit-center" }}>
                    <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
                        
                    </div>
                </Container>
            </>
        );
    }


}

export default product;
