import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Reg from './register';
import { Button, Container, Form, Input, Row } from 'reactstrap';

import { BrowserRouter as  Switch, Route, Link } from 'react-router-dom';

class Login extends Component {
    
    constructor() {
        
        super();
        
        
        this.state = {
            
            Email: '',
            
            Password: ''

        }
        
        
        this.Password = this.Password.bind(this);
        
        this.Email = this.Email.bind(this);
        
        this.handleSubmit = this.handleSubmit.bind(this);

        
    }
    
    
    Email(event) {
        
        this.setState({ Email: event.target.value })
        
    }
    
    Password(event) {
        
        this.setState({ Password: event.target.value })
        
    }
    
    handleSubmit = event => {
        event.preventDefault();

        const user = {
            Email: this.state.Email,
            Password: this.state.Password
        };
       
       
        axios.post('http://0f2c5fd62611.ngrok.io/login', { user })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
    }

    
render() {
    
    
    return (
        
        < div className = "form" >

            < Container className="form-container" >
            
            < Row className = "form" >
                
            
                    < Form onSubmit={this.handleSubmit}>
            
          
                
                                            < div className="login-check" >
                    
    Login
    
                             </div >
        
                                       
        
  
            
    
         < Input className="mb-4" type = "text" onChange = { this.Email } placeholder = "Enter Email" />
            
                                         
         < Input className="mb-4" type = "password" onChange = { this.Password } placeholder = "Enter Password" />
            
        
        < Button className="b-login" onClick = { this.login } color="secondary" block > Login</Button >

                        <div className="login-check"> You don't have an account, yet? < Link to={'/register'} className="nav-link2" pathname="Sign Up!" > Register </Link >
                            < Switch >

                                < Route path='/register' component={Reg} />

                            </Switch >
                        </div>

                                    </Form >
                     
                    </Row >
        
                </Container >
        
            </div >
        
        );
    
}

}


export default Login;
