import tracker from '../src/api/tracker';
import React, { Component } from 'react';
import CanvasJSReact from './canvasjs.react';
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class sales_manager extends Component {
    constructor() {
        super()
        this.state = {
            product: [],
            quantity: ""

        }
    }
    componentDidMount = async () => {
        try {
            const response = await tracker.get("product/all");
            const arrayproduct = response.data;
            this.setState({ product: arrayproduct });
            console.log("state", this.state.product);

        } catch (e) { }

    }
    handleQuantityData = () => {

        var product = [];
        if (this.state.products.length !== 0) {
            var productitem;

            for (var i = 0; i < this.state.product.length; i++) {
                console.log(this.state.product[i]);
                const quantity = this.state.product[i].countInStock;
                const name = this.state.product[i].name;
                productitem = { label: name, y: quantity };
                product.push(productitem);

            }
            console.log(product);
            return product;
        }
    }
    render() {
        const option_column = {
            title: {
                text: "Products and Their Quantities"
            },
            data: [
                {
                    // Change type to "doughnut", "line", "splineArea", etc.
                    type: "column",
                    dataPoints: this.handleQuantityData
                }
            ]
        }



        const option_line = {
            animationEnabled: true,
            exportEnabled: true,
            theme: "light2", // "light1", "dark1", "dark2"
            title: {
                text: "Sales Rate by Week of Year"
            },
            axisY: {
                title: "Sales Rate",
                suffix: "%"
            },
            axisX: {
                title: "Week of Year",
                prefix: "W",
                interval: 2
            },
            data: [{
                type: "line",
                toolTipContent: "Week {x}: {y}%",
                dataPoints: [
                    { x: 1, y: 64 },
                    { x: 2, y: 61 },
                    { x: 3, y: 64 },
                    { x: 4, y: 62 },
                    { x: 5, y: 64 },
                    { x: 6, y: 60 },
                    { x: 7, y: 58 },
                    { x: 8, y: 59 },
                    { x: 9, y: 53 },
                    { x: 10, y: 54 },
                    { x: 11, y: 61 },
                    { x: 12, y: 60 },
                    { x: 13, y: 55 },
                    { x: 14, y: 60 },
                    { x: 15, y: 56 },
                    { x: 16, y: 60 },
                    { x: 17, y: 59.5 },
                    { x: 18, y: 63 },
                    { x: 19, y: 58 },
                    { x: 20, y: 54 },
                    { x: 21, y: 59 },
                    { x: 22, y: 64 },
                    { x: 23, y: 59 }
                ]
            }]
        }
        return (
            <>
            <div>
                <CanvasJSChart options={option_line}
                /* onRef={ref => this.chart = ref} */
                />
                {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
            </div>
<br/>

            <div>
                <CanvasJSChart options={option_column}
                /* onRef={ref => this.chart = ref} */
                />
                {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
                </div>
                </>
		);
    }
}
export default sales_manager;
