import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import { TiTick } from 'react-icons/ti';
import tracker from '../src/api/tracker';
class sm_invoices extends Component {
    constructor() {
        super()
        this.state = {
            orders: [],
            selected_status:[],
        }
        this.status=this.status.bind(this);
    }

    componentDidMount = async () => {
        try {
            const response = await tracker.get("order/all");
            const orderarray = response.data;
            this.setState({ orders: orderarray });
            console.log("state", this.state.orders);


        } catch (e) {
            console.log(e);
        }

    }
    handleChange = async (id) => {

        const statusarray = [...this.state.selected_status];
        const status = statusarray.find(x => x.id === id);
        console.log("status: ", status);
        if (status === "Cancelled") {
            const response = await tracker.delete("order/" + id);
            console.log("delete response:", response);
        }
        else {
            const response = await tracker.put("order/" + id, {
                status: status,
            });
            console.log("status update response:", response);
        }

    }

