        else selected_status.push({ value: e.target.value });
        this.setState(
            {
                selected_status
            },
            () => {
                console.log(this.state.selected_status);
            }
        );
    }
    handleOrders = async () => {
      
        try {
            if (this.state.orders.length !== 0) {
                var invoices = [];
          
