                        const address = this.state.orders[i].address;
                        const id = this.state.orders[i]._id;
                        const res = await tracker.get('/basket/' + basketid);
                        console.log(res);
                        const totalprice = res.data.productList.totalprice;
                   
                        console.log(id);
                        item = (
                            <tr onClick={() => {
                                this.props.history.push("/sm_invoice_details/"  + id);
                            }}>
                                <td>{id}</td>
                                <td>06.01.2021</td>
                                <td>{email}</td>
                                <td>{userid}</td>
                                <td>{totalprice}</td>
                                <td  ><div className='Selectopt'>
                                    <select defaultValue={status} onChange={this.handleStatus(id)} className='Selectopt'>
                                        <option disabled value="Default">Select status</option>
                                        <option value="Cancelled">Cancelled</option>
                                        <option value="Ordered">Ordered</option>
                                        <option value="On the way">On the way</option>
                                        <option value="Shipped">Shipped</option>
                                    </select>
                                </div></td>
                                <td><TiTick onClick={this.handleChange(id)} /></td>
                            </tr>

