            }
           
        }
        catch (e) {
            console.log(e)
        }

    }
    status(e) {
        this.setState({ status: e.target.value });
        console.log(this.state.status);
    }

    render() {
        return (
            <div >
            <Table responsive striped bordered hover variant="dark">
                <thead>
                    <tr>
                        <td>Invoice ID</td>
                        <td>Invoice Date</td>
                            <td>Email</td>
                            <td>User</td>
                        <td>Total Price</td>
                            <td>Status</td>
                            <td>Save</td>
                    </tr>
                </thead>
                    <tbody>
                        <tr>
                        <td>1</td>
                        <td>06.01.2021</td>
                        <td>Gulcin Uras</td>
                        <td>100$</td>
                            <td  ><div className='Selectopt'>
                                <select value={this.state.status} onChange={this.status} className='Selectopt'>
                                    <option>Select status</option>
                                    <option value="Cancelled">Cancelled</option>
                                    <option value="Ordered">Ordered</option>
                                    <option value="On the way">On the way</option>
                                    <option value="Shipped">Shipped</option>
                                </select>
                            </div></td>
                        </tr>
                        {this.handleOrders()}
                </tbody>
            </Table>
            
            </div >

            
            );
    }
}
export default sm_invoices;
