import React, { Component } from "react";
import { Button, Container, Form, Input, Row } from 'reactstrap';
import tracker from '../src/api/tracker';
import Tab from 'react-bootstrap/Tab';
import { BrowserRouter as Switch, Route, Link } from 'react-router-dom';

class Checkout extends Component() {
    constructor() {
        super();
        this.state = {
            Email: "",
            address: "",

            cvc: '',
            expiry: '',
            focus: '',
            name: '',
            number: '',
        };
    }

    handleInputFocus = (e) => {
        this.setState({ focus: e.target.name });
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;

        this.setState({ [name]: value });
    }

    handleAddress = () => {
        
    }
    handleEmail = () => {

    }
    handleAccount = () => {
        var loggedin = localStorage.getItem('token');
        console.log("token: ", loggedin);
        var address;
        try {
            if (loggedin !== null) {
                console.log("Logged in");
                address = (
                    <div>
                    < Input className="mb-4" type="text" onChange={this.handleAddress} placeholder="Enter Email" />
                        < Input className="mb-4" type="text" onChange={this.handleEmail} placeholder="Enter Address" />
                    </div>
                );
            }
            else {
                address = (
                    <div>
                    < Input className="mb-4" type="text" onChange={this.Email} placeholder="Enter Email" />
                        < Input className="mb-4" type="text" onChange={this.address} placeholder="Enter Address" />
                    </div>
                );

            }
            return address;
        } catch (e) {
            console.log(e);
        }
    }



    render() {
        return (
            < div className="form" >

                < Container className="form-container" >

                    <Tabs defaultActiveKey="Address" >
                        <Tab eventKey="Address" title="Address">
                            <Sonnet />
                            {this.handleAccount()}
                        </Tab>
                        <Tab eventKey="Payment" title="Payment">
                            <Sonnet />
                            <div id="PaymentForm">
                                <Cards
                                    cvc={this.state.cvc}
                                    expiry={this.state.expiry}
                                    focused={this.state.focus}
                                    name={this.state.name}
                                    number={this.state.number}
                                />
                                <form>
                                    <input
                                        type="tel"
                                        name="number"
                                        placeholder="Card Number"
                                        onChange={this.handleInputChange}
                                        onFocus={this.handleInputFocus}
                                    />
          ...
        </form>
                            </div>
                        </Tab>
                      
                    </Tabs>

                </Container >

            </div >
            );
    }

}
export default Checkout;
