import React, { Component } from "react";
import "react-alice-carousel/lib/alice-carousel.css";
import "./SelectOpt";
import "./Checkbox";
import ReactStars from "react-rating-stars-component";
import { CardLink, Col, Container, Form, Input, Modal, Row } from "reactstrap";

import tracker from "../src/api/tracker";

class Comments extends Component {
    constructor(props) {
        super(props);
        console.log("props:", this.props);
        this.state = {
            notloggedin: true,
            rating: 0,
            avg_rate: 0,
            txt: "Type your comment here!",
            comments: [],
            commentId: "",
            productId:"",
        }
    }
    componentDidMount = async () => {
        console.log("props", this.props);
     //   if (this.props.commentId !== null) {
          /*  this.setState({ commentId: this.props.commentId });
        }
        if (this.props.productId !== null) {
            this.setState({ productId: this.props.productId });
        } */
        var loggedin = localStorage.getItem('userId');
        console.log("userId: ", loggedin);
        if (loggedin === null) {
            this.setState({ notloggedin: true });

        }
        else {
            this.setState({ notloggedin: false });
        }
        const { pid } = this.props.location.state.productId;
        this.setState({ productId: { pid } });
   
        console.log( "pid:", this.state.productId);
        if (this.state.commentId !== "") {
            try {
                const commentresponse = await tracker.get("comment/" + this.state.commentId);

                console.log("commentresponse:", commentresponse);
                const arraycomments = commentresponse.data;
                this.setState({ comments: arraycomments });
                console.log("state", this.state.comments);

            } catch (e) {
                console.log(e);
            }
        }
    }
    handleValue = (e) => {
        this.setState({ txt: e.target.value });
    }
    handleRating = (e) => {
        console.log("handlerating: ", e)
        this.setState({ rating: e });
    }
    handleComment = async () => {

        const id = this.state.commentId;

        if (!this.state.notloggedin) {
            try {
                if (id === null) {
                    console.log("rating: ", this.state.rating);
                    console.log("text: ", this.state.txt);
                    console.log("id: ", id);
                    const response = await tracker.post("comment/", {
                        product: this.state.productId,
                        totalRate: this.state.rating,
                        comments: [
                            {
                                user: "5fd772117dca07d084b5227b", //duzeltilecek
                                rating: this.state.rating,
                                text: this.state.txt,

                            },
                        ],
                    });

                }
                else {
                    const response = await tracker.put("comment/" + id, {

                        comments: [
                            {
                                user: "5fd772117dca07d084b5227b", //duzeltilecek
                                rating: this.state.rating,
                                text: this.state.txt,

                            },
                        ],
                    });


                }


            } catch (e) {
                console.log(e);
            }
        }

    };
  
    otherReviews = () => {
        console.log("otherReviews");
        var com_array = [];

        try {

            if (this.state.comments.length !== 0) {
                var commentitem;
                for (var i = 0; i < this.state.comments.length; i++) {
                    console.log(this.state.comments[i]);
                    const commentid = this.state.comments[i].commentId;
                    const user = this.state.comments[i].user;
                    const rating = this.state.products[i].rating;
                    const text = this.state.products[i].text;

                    commentitem = (
                        <div>
                            <ReactStars
                                count={5}
                                value={rating}
                                disableButtonsControls={true}
                                size={24}
                                isHalf={false}
                                emptyIcon={<i className="far fa-star"></i>}
                                halfIcon={<i className="fa fa-star-half-alt"></i>}
                                fullIcon={<i className="fa fa-star"></i>}
                                activeColor="#ffd700"
                            />
                            <p>{text}</p>
                        </div>
                    );
                    com_array.push(commentitem);
                }


            }
            return (com_array);
        } catch (e) { }



    }
    
    render() {
        return (
            < div className="form" >

                < Container className="form-container" >

                    < Row className="form" >
                        < Form >
                            <div style={{ marginLeft: "-72vh" }}>Rate the product</div>
                            <ReactStars
                                count={5}
                                value={this.state.rating}
                                onChange={this.handleRating}
                                size={24}
                                isHalf={false}
                                emptyIcon={<i className="far fa-star"></i>}
                                halfIcon={<i className="fa fa-star-half-alt"></i>}
                                fullIcon={<i className="fa fa-star"></i>}
                                activeColor="#ffd700"
                            />
                            <div>Add a comment</div>
                            <div>
                                <input
                                    style={{ width: "300px", height:"100px", size: "relative", margin:"2rem"}}
                                    type="text"
                                    onChange={this.handleValue}
                                />
                                <br />
                                {this.handleLogin}
                                {this.state.notloggedin ? <div className="row" style={{ color: "#c29fdc", justifyContent: "space-evenly" }}><p>Only subscribed users can comment!</p><button disabled="true" onClick={this.handleComment}>Send</button></div> : <div className="card"><button className="b-login" onClick={this.handleComment}>Send</button></div>}


                            </div>

                            <div className="card"  >
                                <CardLink
                                    onClick={() => {
                                        this.otherReviews();
                                    }}
                                >
                                    <button className="b-login">See other reviews</button>
                                </CardLink>
                            </div>
                        </Form >

                    </Row >

                </Container >

            </div >

        );

    }
}
export default Comments;
