import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import tracker from '../src/api/tracker';
import { CardLink, Button, Container, Form, Input, Row, Col } from 'reactstrap';
class sm_invoice_details extends Component {
    constructor() {
        super()
        this.state = {
            id: "",
            arr: [],
            status: "",
            details:false
        }
    }
    handleChange = async (id, oldstatus) => {

        const status = this.state.status;
        console.log("status: ", status);
        console.log("oldstatus: ", oldstatus);
        if (oldstatus !== status) {
            if (status === "Cancelled") {
                const response = await tracker.delete("order/" + id);
                console.log("delete response:", response);
            }
            else {
                const response = await tracker.put("order/" + id, {
                    status: status,
                });
                console.log("status update response:", response);
            }

        }
        else {

        }
    

    }

    componentDidMount = async (e) => {
        var id = this.props.match.params.id;
        this.setState({ id: id });
        const response = await tracker.get('/order/' + id);
        console.log("responsee", response)
        const email = response.data.email;
        const status = response.data.status;
        await this.setState({ status: status });
        const userid = response.data.user;
        const address = response.data.address;
        var arr=[];
        if (response) {
            console.log("responsee", response)
            /////////////gelen arrayproducta g�re g�r�n�m ayarla
            console.log(response.data)
            console.log(response.data.basket)
            const res = await tracker.get('/basket/' + response.data.basket);
            console.log(res);
            const totalprice = res.data.productList.totalprice;
            var parray = [];
            debugger;
            for (var j = 0; j < res.data.productList.productArray.length; j++) {
                const name = res.data.productList.productArray[j].name;
                const price = res.data.productList.productArray[j].price;
                const size = res.data.productList.productArray[j].size;
                const q = res.data.productList.productArray[j].quantity;
                const prod = [name, price, size, q]
                console.log("q", q)
                parray.push(prod);
                console.log("prod", prod)

            };


            var item = (
                <div style={{ marginTop: "20px" }}>

                    <Row>
                        <Table striped responsive bordered hover variant="dark">
                            <thead>
                              
                                <tr>
                                    <th>name</th>
                                    <th>price</th>
                                    <th>size</th>
                                    <th>quantity</th>
                                </tr>
                            </thead>
                            <tbody>

                                {
                                    parray.map((numList, i) => (
                                        <tr key={i}>
                                            {
                                                numList.map((num, j) =>
                                                    <td key={j}>{num}</td>
                                                )
                                            }
                                        </tr>
                                    ))
                                }
                           
                                <tr><h5>Address: {address} </h5></tr>
                            </tbody>
                            <tr><h4 >Invoice ID #{this.state.id} from User: {userid}</h4></tr>
                            <tr><h5>Total Price: ${totalprice} </h5></tr>
                            <tr><h5>Email: {email} </h5></tr>
                            <tr><div className='Selectopt'>
                                <select defaultValue={this.state.status} onChange={this.status} className='Selectopt'>
                                    <option disabled value="Default">Select status</option>
                                    <option value="Cancelled">Cancelled</option>
                                    <option value="Ordered">Ordered</option>
                                    <option value="On the way">On the way</option>
                                    <option value="Shipped">Shipped</option>
                                </select>
                            </div></tr>
                        </Table>
                    </Row>
                    <button color="Green" onClick={() => {this.handleChange(id, response.data.status)}}>Save</button>
                </div>
                
                );
              
            arr.push(item);
            await this.setState({ arr: arr });
            await this.setState({details: true})
         


        }
        else {

        }
    }

    render() {
        return (
            <>
                {this.state.details ? this.state.arr : null}
                </>
            
            );
    }
}
export default sm_invoice_details;
