import React, { Component } from "react";
import {Button, Container, CardLink, Input, Row, Col } from 'reactstrap';

import tracker from '../src/api/tracker';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';

class Checkout extends Component {
    constructor() {
        super();
        this.state = {
            Email: "",
            address: "",

            cvc: '',
            expiry: '',
            focus: '',
            name: '',
            number: '',
        };
    }

    handleInputFocus = (e) => {
        this.setState({ focus: e.target.name });
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;

        this.setState({ [name]: value });
    }

    handleAddress = () => {
        
    }
    handleEmail = () => {

    }
    handleAccount = () => {
        var loggedin = localStorage.getItem('token');
        console.log("token: ", loggedin);
        var address;
        try {
            if (loggedin !== null) {
                console.log("Logged in");
                address = (
                    <div>
                    < Input className="mb-4" type="text" onChange={this.handleAddress} placeholder="Enter Email" />
                    < Input className="mb-4" type="text" onChange={this.handleEmail} placeholder="Enter Address" />
                    </div>
                );
            }
            else {
                address = (
                    <div>
                    < Input className="mb-4" type="text" onChange={this.Email} placeholder="Enter Email" />
                    < Input className="mb-4" type="text" onChange={this.address} placeholder="Enter Address" />
                    </div>
                );

            }
            return address;
        } catch (e) {
            console.log(e);
        }
    }



    render() {
        return (
            < div className="form" >

                < Container className="form-container" >

                    <Tabs defaultActiveKey="Address" >
                        <Tab eventKey="Address" title="Address">
                          
                            {this.handleAccount()}
                        </Tab>
                        <Tab eventKey="Payment" title="Payment">
                            <Container>
                                <div id="PaymentForm">
                                    <Row style={{ padding: "2rem" }}>
                                        <Col>
                                    <Cards
                                    cvc={this.state.cvc}
                                    expiry={this.state.expiry}
                                    focused={this.state.focus}
                                    name={this.state.name}
                                    number={this.state.number}
                                            />
                                        </Col>
                                        <Col>
                                <form>
                                            <input className="mb-4" 
                                        type="tel"
                                        name="number"
                                        placeholder="Card Number"
                                        onChange={this.handleInputChange}
                                        onFocus={this.handleInputFocus}
                                        />
                                  
                                            <input className="mb-4" 
                                            type="tel"
                                            name="name"
                                            placeholder="Card Holder"
                                            onChange={this.handleInputChange}
                                            onFocus={this.handleInputFocus}
                                        />
                                    
                                            <input className="mb-4" 
                                            type="tel"
                                            name="expiry"
                                            placeholder="Expiry Date"
                                            onChange={this.handleInputChange}
                                            onFocus={this.handleInputFocus}
                                        />


                                            <input className="mb-4" 
                                            type="tel"
                                            name="cvc"
                                            placeholder="CVC"
                                            onChange={this.handleInputChange}
                                            onFocus={this.handleInputFocus}
                                        />
                                     
                                           
                                     

                                            </form></Col>
                                    </Row>
                                    <Row> <CardLink > <Button onClick="" >Proceed Payment</Button > </CardLink></Row>
                                </div>
                                </Container>
                        </Tab>
                      
                    </Tabs>

                </Container >

            </div >
            );
    }

}
export default Checkout;
