import React, { Component } from 'react';
import Icon from '@material-ui/core/Icon';
import DeleteForeverRoundedIcon from '@material-ui/icons/DeleteRounded';
import './App.css';
import { Button, Container, Form, Input, Row, Col, CardLink } from 'reactstrap';
import tracker from '../src/api/tracker';

class user_info extends Component {
    constructor() {

        super();


        this.state = {
            email: "",
            address: "",
            hasAddress: false,
        }
    }
    componentDidMount = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        try {
            const response = await tracker.get("address/" + loggedin);
            if (response.data !== null) {
                console.log("response.data not null, return address");
                this.setState({ address: response.data });
                this.setState({ hasAddress: true });

            }
        } catch (e) {
            console.log(e);
        }
    }
    componentDidUpdate = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        try {
            const response = await tracker.get("address/" + loggedin);
            if (response.data !== null) {
                console.log("response.data not null, return address");
                this.setState({ address: response.data });
            }
        } catch (e) {
            console.log(e);
        }
    }
    handleEmail = async () => {


    }
    handleDeleteAddress = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        const response = await tracker.delete('adress/' + loggedin);
        console.log("handleDeleteAddress: ", response);
        this.setState({ address: '' });
        this.setState({ hasAddress: false });
    }

    handleEditAddress = async () => {

        var loggedin = localStorage.getItem('token');

        console.log("token: ", loggedin);
        const response = await tracker.put('adress/' + loggedin, { adress: this.state.address });
        console.log("handleAddAddress: ", response);

    }

    handleaddAddress = async () => {
        const response = await tracker.post('adress/', { adress: this.state.address });
        console.log("handleaddAddress: ", response);
    }
   
   
    render() {
        return (
            <>
                < div className="form" >

                    < Container className="form-container" >

                        < Row className="form" >
                            < Form >
                                <Row><h5>Your email address: </h5>{this.handleEmail}</Row>
                                <Row><h5>Your delivery address:</h5></Row>
                                <Row>{this.state.hasAddress ?
                                    <div>
                                        <Col>
                                            <Row><DeleteForeverRoundedIcon onClick={this.handleDeleteAddress} /><CardLink onClick={this.handleEditAddress}>Edit</CardLink></Row>
                                            <Row>{this.state.address}</Row>
                                        </Col>
                                    </div>
                                    : <div>
                                        <Col>
                                            <Row><label>Add an address!</label></Row>

                                            < Input type="textarea" style={{ padding: "3rem", justifyContent: "space-around" }} onChange={this.state.address} placeholder="Enter Your Address Here" />
                                            <div className="row" style={{ justifyContent: "center" }}>
                                                < Button style={{ width: "auto" }} className="b-login"
                                                    onClick={this.handleaddAddress} color="secondary" block >Add</Button >
                                            </div>
                                        </Col>
                                    </div>}</Row>


                            </Form >

                        </Row >

                    </Container >

                </div >
            </>

        );
    }
}
export default user_info;
