import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import React, { Component ,useEffect} from 'react';
import Logo from './images/mylogo.png';
import { withRouter } from 'react-router';
import 'bootstrap/dist/css/bootstrap.min.css';

import tracker from '../src/api/tracker'
var header;

class Header extends Component {
    static navigationOptions = ({ navigation }) => {
        return {
          header: navigation.getParam('otherParam', 'A Nested Details Screen'),
        };
      };
    constructor() {
        super();
        this.state = {
            loggedin: false,
            needRerender:false,
            email:"",
            localstorage:"",
            Header: header,
            salesManager: false,
            productManager:false,
        }
    }
    logout = () => {
        localStorage.clear('email');
        this.props.history.push("/Homepage");
        this.setState({ loggedin: false , email: ""})
    }


    componentDidMount = async () => {
        var email = localStorage.getItem('email');
        console.log("email: ", email);


        if (email !== null) {
            console.log("Logged in as ", email);
            this.setState({ loggedin: true , email: email})

        }
        else {

            this.setState({ loggedin: false })

        }
        var sm = localStorage.getItem('isSalesManager');
        if (sm === true) {
            this.setState({ salesManager: true });
        }
        else {
            this.setState({ salesManager: false });
        }

        var pm = localStorage.getItem('isProductManager');
        if (pm === true) {
            this.setState({ productManager: true });
        }
        else {
            this.setState({ productManager: false });
        }
    };
    // componentDidUpdate = async () => {
    //     var email = localStorage.getItem('email');
    //     console.log("email: ", email);


    //     if (email !== null) {
    //         console.log("Logged in as ", email);
    //         this.setState({ loggedin: true , email: email})

    //     }
    //     else {

    //         this.setState({ loggedin: false })

    //     }

    // };

    isloggedin = () => {
        var email = localStorage.getItem('email');
        console.log("email: ", email);


        if (email !== null) {
            console.log("Logged in as ", email);
            this.setState({ loggedin: true , email: email})

        }
        else {

            this.setState({ loggedin: false })

        }

    }

    handleSearch = async (e) => {
      console.log(e.target.value)
        this.setState({search: e.target.value});
        console.log(this.state.search)
        if(this.state.search.length < 3)
        this.setState({searchPlaceholder:"Please enter at least 2 character"})
        else
        {
        this.setState({searchPlaceholder:"Search"})
        const response = await tracker.get('/all/search/' + this.state.search);
        console.log(response.data)
        }
    
    }
    render() {
        return (
            <>
            {this.isloggedin}
                <Navbar collapseOnSelect expand="lg" bg="light" variant="light" {...this.props}>
                    <Navbar.Brand href="/Homepage"><img src={Logo} width="48px" heght="36px" /></Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="/Login">Login</Nav.Link>
                            <Nav.Link href="/register">Register</Nav.Link>

                        </Nav>
                        {this.state.email}
                        <Nav>
                            <form class="form-inline my-2 my-lg-0">
                                <input class="form-control mr-sm-2" onChange ={this.handleSearch}type="search" placeholder="Search" aria-label="Search" />
                                <button class="btn btn-outline-success my-2 my-sm-0" color="text-warning" type="submit">Search</button>
                            </form>



                            {this.state.loggedin ? <NavDropdown title="Account" id="collasible-nav-dropdown">
                                <NavDropdown.Item href="/user_info">User Information</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.2">Comments</NavDropdown.Item>
                                <NavDropdown.Item href="/changePassword">Change Password</NavDropdown.Item>
                                <NavDropdown.Item href="#" onClick={this.logout}>Log Out</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="/sm_invoices">Sales Manager</NavDropdown.Item>
                                <NavDropdown.Item href="/product_manager">Product Manager</NavDropdown.Item> </NavDropdown>
                                :
                                <NavDropdown title="Account" id="collasible-nav-dropdown">  <NavDropdown.Item href="/Login">Login</NavDropdown.Item>
                                    <NavDropdown.Item href="/register">Register</NavDropdown.Item> </NavDropdown>
                            }




                            <Nav.Link href="/sales_manager">Sale Charts</Nav.Link>
                            <Nav.Link href="/basket">Basket</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
                    <Navbar.Brand href="/Homepage">FRIENDYOL</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">

                            <NavDropdown title="Woman" id="collasible-nav-dropdown">
                                <NavDropdown.Item href="/womenHeels">Heels</NavDropdown.Item>
                                <NavDropdown.Item href="/womenSneakers">Sneakers</NavDropdown.Item>
                                <NavDropdown.Item href="/womenBoots">Boots</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="/womenAll">All Products</NavDropdown.Item>
                            </NavDropdown>
                            <NavDropdown title="Man" id="collasible-nav-dropdown">
                                <NavDropdown.Item href="/menClassics">Classics</NavDropdown.Item>
                                <NavDropdown.Item href="/menSneakers">Sneakers</NavDropdown.Item>
                                <NavDropdown.Item href="/menBoots">Boots</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="/menAll">All Products</NavDropdown.Item>
                            </NavDropdown>




                        </Nav>

                    </Navbar.Collapse>
                </Navbar>
            </>
        );

    }
}
export default withRouter(Header);
