import React, { Component } from 'react';
import './App.css';
import { Button, Container, Form, Input, Row, Col } from 'reactstrap';
import tracker from '../src/api/tracker';
import { BiPencil, BiTrash } from 'react-icons/bi';

class user_info extends Component {
    constructor() {

        super();


        this.state = {
            email: "",
            address: "",
            hasAddress: true,
            EditClicked: false,
            DeleteClicked:false,
        }
    }
    componentDidMount = async () => {
        var mail = localStorage.getItem('email');
        this.setState({ email: mail });
        var loggedin = localStorage.getItem('userId');

        console.log("userId: ", loggedin);
        try {
            const response = await tracker.get("adress/" + loggedin);
            console.log("response",response.data);
            if (response.data !== "") {
                console.log("response.data not null, return address");
                this.setState({ address: response.data });
                this.setState({ hasAddress: true });

            }
            else {
                this.setState({ address: "" });
                this.setState({ hasAddress: false });
            }
        } catch (e) {
            console.log(e);
        }
    }
  
    handleDeleteAddress = async () => {

        var loggedin = localStorage.getItem('userId');

        const response = await tracker.delete('adress/' + loggedin);
        console.log("handleDeleteAddress: ", response);
        this.setState({ address: '' });
        this.setState({ hasAddress: false });

        this.setState({ DeleteClicked: false });
    }

    handleEditAddress = async () => {
   
        var loggedin = localStorage.getItem('userId');

        const response = await tracker.put('adress/' + loggedin, { adress: this.state.address });
        console.log("handleAddAddress: ", response);
        this.setState({ EditClicked: false });

    }

    handleaddAddress = async () => {
        var loggedin = localStorage.getItem('userId');
        const response = await tracker.post('adress/' + loggedin, { adress: this.state.address });
        console.log("handleaddAddress: ", response);
    }
    handleAddress = () => {
        var addressItem;
        console.log(this.state.address);
        console.log(this.state.EditClicked);
        console.log(this.state.DeleteClicked);
        if (this.state.address !== "" && !this.state.EditClicked) {
            addressItem = (<div>
                <Col>
                    <Row style={{ justifyContent: "space-evenly" }}><BiTrash onClick={this.setState({ DeleteClicked: true })} /><BiPencil onClick={this.setState({ EditClicked: true })} /></Row>
                    <Row><h5>Your delivery address: {this.state.address}</h5></Row>
                </Col>
            </div>);
            return addressItem;
        }
        else if (this.state.address !== "" && this.state.EditClicked) {
            addressItem = (< div >
                <Col>
                    <Row><label style={{ justifyContent: "center" }}>Enter your new address.</label></Row>

                    < Input type="textarea" style={{ padding: "3rem", margin: "0.5rem", justifyContent: "space-around" }} onChange={this.state.address} placeholder="Enter Your Address Here" />
                    <div className="row" style={{ justifyContent: "center" }}>
                        < Button style={{ width: "auto" }} className="b-login"
                            onClick={this.handleEditAddress} color="secondary" block >Add</Button >
                    </div>
                </Col>
            </div>);
            return addressItem;
        }
        else if (this.state.address !== "" && this.state.DeleteClicked) {
            addressItem = (<div>
                <Col>
                    <Row><label style={{ justifyContent: "center" }}>Your address has been deleted!</label></Row>
                    <Row><label style={{ justifyContent: "center" }}>Add an address!</label></Row>

                    < Input type="textarea" style={{ padding: "3rem", margin: "0.5rem", justifyContent: "space-around" }} onChange={this.state.address} placeholder="Enter Your Address Here" />
                    <div className="row" style={{ justifyContent: "center" }}>
                        < Button style={{ width: "auto" }} className="b-login"
                            onClick={this.handleaddAddress} color="secondary" block >Add</Button >
                    </div>
                </Col>
            </div>);
            return addressItem;
        }
        else if (this.state.address === "" && !this.state.DeleteClicked && !this.state.EditClicked) {
            addressItem = (< div >
                <Col>
                    <Row><label style={{ justifyContent: "center" }}>Add an address!</label></Row>

                    < Input type="textarea" style={{ padding: "3rem", margin: "0.5rem", justifyContent: "space-around" }} onChange={this.state.address} placeholder="Enter Your Address Here" />
                    <div className="row" style={{ justifyContent: "center" }}>
                        < Button style={{ width: "auto" }} className="b-login"
                            onClick={this.handleaddAddress} color="secondary" block >Add</Button >
                    </div>
                </Col>
            </div>);
            return addressItem;
        }
    
    }

    render() {
        console.log("address", this.state.address);
        return (
            <>
                < div className="form" >

                    < Container className="form-container" >

                        < Row className="form" >
                            < Form >
                                <Row><h5>Your email address: {this.state.email}</h5></Row>

                                <Row>{this.handleAddress}</Row>


                            </Form >

                        </Row >

                    </Container >

                </div >
            </>

        );
    }
}
export default user_info;
