import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";

import Sneakers from "./images/kadınspor.jpeg";
import tracker from "../src/api/tracker";
import { CardLink, Container, Row } from "reactstrap";

import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";

import womenSneakers1 from "./images/womenSneakers_1.jpg";
import womenSneakers1_2 from "./images/womenSneakers_1_2.jpg";
import womenSneakers1_3 from "./images/womenSneakers_1_3.jpg";
import womenSneakers2 from "./images/womenSneakers_2.jpg";
import womenSneakers2_2 from "./images/womenSneakers_2_2.jpg";
import womenSneakers2_3 from "./images/womenSneakers_2_3.jpg";
import womenSneakers3 from "./images/womenSneakers_3.jpg";
import womenSneakers3_2 from "./images/womenSneakers_3_2.jpg";
import womenSneakers3_3 from "./images/womenSneakers_3_3.jpg";
import womenSneakers4 from "./images/womenSneakers_4.jpg";
import womenSneakers4_2 from "./images/womenSneakers_4_2.jpg";
import womenSneakers4_3 from "./images/womenSneakers_4_3.jpg";
import womenSneakers5 from "./images/womenSneakers_5.jpg";
import womenSneakers5_2 from "./images/womenSneakers_5_2.jpg";
import womenSneakers5_3 from "./images/womenSneakers_5_3.jpg";
import womenSneakers6 from "./images/womenSneakers_6.jpg";
import womenSneakers6_2 from "./images/womenSneakers_6_2.jpg";
import womenSneakers6_3 from "./images/womenSneakers_6_3.jpg";
import womenSneakers7 from "./images/womenSneakers_7.jpg";
import womenSneakers7_2 from "./images/womenSneakers_7_2.jpg";
import womenSneakers7_3 from "./images/womenSneakers_7_3.jpg";
import womenSneakers8 from "./images/womenSneakers_8.jpg";
import womenSneakers8_2 from "./images/womenSneakers_8_2.jpg";
import womenSneakers8_3 from "./images/womenSneakers_8_3.jpg";
import womenSneakers9 from "./images/womenSneakers_9.jpg";
import womenSneakers9_2 from "./images/womenSneakers_9_2.jpg";
import womenSneakers9_3 from "./images/womenSneakers_9_3.jpg";
import womenSneakers10 from "./images/womenSneakers_10.jpg";
import womenSneakers10_2 from "./images/womenSneakers_10_2.jpg";
import womenSneakers10_3 from "./images/womenSneakers_10_3.jpg";
import womenSneakers11 from "./images/womenSneakers_11.jpg";
import womenSneakers11_2 from "./images/womenSneakers_11_2.jpg";
import womenSneakers11_3 from "./images/womenSneakers_11_3.jpg";
// import womenSneakers12 from "./images/womenSneakers_12.jpg";

class womenSneakers extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      images: Sneakers,
    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/female/category/Sneakers");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };

 
  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        console.log(this.state.products[i]);
        const id = this.state.products[i]._id;
        const image = this.state.products[i].image;
        const price = this.state.products[i].price[0];
        const name = this.state.products[i].name;

        console.log("image", image);
        const imagearray = [
          [womenSneakers1, womenSneakers1_2, womenSneakers1_3],
          [womenSneakers2, womenSneakers2_2, womenSneakers2_3],
          [womenSneakers3, womenSneakers3_2, womenSneakers3_3],
          [womenSneakers4, womenSneakers4_2, womenSneakers4_3],
          [womenSneakers5, womenSneakers5_2, womenSneakers5_3],
          [womenSneakers6, womenSneakers6_2, womenSneakers6_3],
          [womenSneakers7, womenSneakers7_2, womenSneakers7_3],
          [womenSneakers8, womenSneakers8_2, womenSneakers8_3],
          [womenSneakers9, womenSneakers9_2, womenSneakers9_3],
          [womenSneakers10, womenSneakers10_2, womenSneakers10_3],
          [womenSneakers11, womenSneakers11_2, womenSneakers11_3],
          //   [womenSneakers12, womenSneakers12, womenSneakers12],
        ];
        const coni = i;
        productitem = (
          <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" }}>
            <CardLink
              onClick={() => {
                this.props.history.push("/product/" + id + "/ws" + "/" + coni);
              }}
              style={{ color: "black" }}
            >
              <AliceCarousel
                disableButtonsControls={true}
                mouseTracking={false}
                responsive
                autoPlay
                autoPlayInterval="3000"
                style={{ height: "40vh", marginBottom: "5vh" }}
              >
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][0]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][1]} alt="High Heels" />
                <img style={{ width: "25vh", height: "25vh" }} src={imagearray[i][2]} alt="High Heels" />
              </AliceCarousel>
              <h4>{name}</h4>
                    <p class="price">${price}</p>
                    <button>Add to Cart</button>
            </CardLink>
          
              
          
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Woman Sneakers</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-between" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default womenSneakers;
