import { ProSidebar, Menu, MenuItem, SubMenu} from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { BiCommentEdit, BiShoppingBag, BiMenu } from "react-icons/bi";
import {  SidebarHeader, SidebarFooter, SidebarContent } from 'react-pro-sidebar';

class product_manager extends Component {
    constructor() {
        super();
        this.state={
            isOpen : false
        }
    }
    render() {
        return (
            <ProSidebar toggled={this.state.isOpen}
            >
                <SidebarHeader>
                   Product Manager
                </SidebarHeader>
                <SidebarContent>
                    <Menu iconShape="square" icon={<BiMenu />}>
                        <MenuItem icon={<BiCommentEdit />}>Comments<Link to="/product_manager_comments" /></MenuItem>
                        <MenuItem icon={<BiShoppingBag />}>Products</MenuItem>
                        {/*  <SubMenu title="Components" >
                        <MenuItem>Component 1</MenuItem>
                        <MenuItem>Component 2</MenuItem>
                    </SubMenu>
                    */}
                    </Menu>
                </SidebarContent>
                <SidebarFooter>
                    {/**
     *  You can add a footer for the sidebar ex: copyright
     */}
                </SidebarFooter>
              
            </ProSidebar>
        );
    }
}
export default product_manager;
