import './App.css';
import tracker from '../src/api/tracker';
import Indeterminate from './Checkbox';
import Table from 'react-bootstrap/Table';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { FaCheckCircle } from "react-icons/fa";
import { SidebarContent } from 'react-pro-sidebar';
import { Link } from 'react-router-dom';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import React, { Component, useEffect } from 'react';
import Logo from './mylogo.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Input } from '@material-ui/core';
class sm_campaigns extends Component {
    constructor() {
        super()
        this.state = {
            products: [],
            updated_prices: [],
            price: 0,
            arr: [],
            change:false
        }
    }
    componentDidMount = async () => {
        try {
            const response = await tracker.get("product/all");
            const arrayproduct = response.data;
            this.setState({ products: arrayproduct });
            console.log("state", this.state.products);

            var prod = [];
            var arr = [];
            if (this.state.products.length !== 0) {
                console.log(this.state.products);
                var item;
                var parray = [];
                for (var i = 0; i < this.state.products.length; i++) {
                    console.log(this.state.products[i]);
                    const id = this.state.products[i]._id;
                    const brand = this.state.products[i].brand;
                    const category = this.state.products[i].category;
                    const countInStock = this.state.products[i].countInStock;
                    const size = this.state.products[i].size;
                    const gender = this.state.products[i].gender;
                    const newprice = this.state.products[i].newprice;
                    const price = this.state.products[i].price;
                    const name = this.state.products[i].name;
                    const description = this.state.products[i].description;

                    var update = (< Input className="mb-4" type="number" onChange={this.state.price} placeholder={newprice} />)
                    const save = (<FaCheckCircle onClick={() => { this.handleChange(id, name, size, category, brand, description, gender, this.state.price) }} />)
                    const prod = [name, id, brand, category, countInStock, size, price, newprice, update, save]
                    console.log(id);
                    console.log(prod);
                    parray.push(prod);
                };

                var item = (
                    <div style={{ marginTop: "20px" }}>

                       
                            <Table striped responsive bordered hover variant="dark">

                                <thead>
                                    <td>Name</td>
                                    <td>Id</td>
                                    <td>Brand</td>
                                    <td>Category</td>
                                    <td>Gender</td>
                                    <td>Count in Stock</td>
                                    <td>Size</td>
                                    <td>Price</td>
                                    <td>New Price</td>
                                    <td>Update Price</td>
                                    <td>Save</td>
                                </thead>
                                <tbody>

                                    {
                                        parray.map((numList, i) => (
                                            <tr key={i}>
                                                {
                                                    numList.map((num, j) =>
                                                        <td key={j}>{num}</td>
                                                    )
                                                }
                                            </tr>
                                        ))
                                    }

                                </tbody>

                            </Table>
               
                    </div>

                );

                arr.push(item);
                await this.setState({ arr: arr });
                await this.setState({ change: true })


            }


        } catch (e) {
            console.log(e);
        }

    }


    handleUpdate = async (id, name, size, category, brand, description, gender, updated_price) => {
        try {
            const response = await tracker.put("product/" + id, {
                name: name,
                size: size,
                category: category,
                brand: brand,
                description: description,
                gender: gender,
                newprice: updated_price,


            })

            console.log(response);
        } catch (e) {
            console.log(e);
        }

    }
  

    render() {
        return (
            <>

                {this.state.change ? this.state.arr : null}
            </>
        );
    }

}
export default sm_campaigns;
