import React, { Component } from 'react';
import './App.css';
import tracker from '../src/api/tracker';
import Indeterminate from './Checkbox';
import Table from 'react-bootstrap/Table';
import { ProSidebar, Menu, MenuItem} from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { BiCommentEdit, BiShoppingBag, BiMenu } from "react-icons/bi";
import { SidebarContent } from 'react-pro-sidebar';
import { BrowserRouter as Link } from 'react-router-dom';

import { Col, Row } from "reactstrap";
class product_manager_comments extends Component {
    constructor(props) {

        super(props);

        this.state = {
            comments: [],
            invalidcomments: [],
        }

    }
    componentDidMount = async () => {
        try {
            const response = await tracker.get("comment/all");
            console.log("all comments response: ", response);
            const comments = response.data;
            this.setState({ comments: comments });

        } catch (e) {
            console.log(e);
        }

    }
    Validate = async (product, id, user, rating, text, totalrating, index) => {
        try {
            const response = await tracker.put("comment/" + id, {
                product: product,
                totalRate: totalrating,
                comments: [
                    {
                        user: user, //duzeltilecek
                        isValid: true,
                        rating: rating,
                        text: text,

                    },
                ],
            });
            var invalidcomments = [...this.state.invalidcomments];
            invalidcomments.splice(index, 1);
            this.setState({ invalidcomments });
        } catch (e) {
            console.log(e);
        }
    }
    getInvalidComments = () => {
        var invalidComments = [];
        var count = -1;
        if (this.state.comments.length !== 0) {
            var comment;
            for (var i = 0; i < this.state.comments.length; i++) {
                console.log(this.state.comments[i]);
                const product = this.state.comments[i].product;
                const totalrating = this.state.comments[i].totalRate;
                for (var j = 0; j < this.state.comments[i].comments.length; j++) {
                    const isValid = this.state.comments[i].comments[j].isValid;
                    const id=0;
                    const user=0;
                    const rating=0;
                    const text="";
                    if (isValid === false) {
                        id = this.state.comments[i].comments[j].commentId;
                        user = this.state.comments[i].comments[j].user;
                        rating = this.state.comments[i].comments[j].rating;
                        text = this.state.comments[i].comments[j].text;
                        count++;
                        comment = (
                            <tr>
                                <td>{product}</td>
                                <td>{id}</td>
                                <td>{user}</td>
                                <td>{rating}</td>
                                <td>{text}</td>
                                <td className='notify' for='cb' onClick={this.Validate(product, id, user, rating, text, totalrating, count)}><Indeterminate /></td>
                            </tr>

                        );
                        invalidComments.push(comment);
                    }
                }
            }

        }
        this.setState({ invalidcomments: invalidComments });
        return invalidComments;
    };
    render() {
        return (
            <div>
                    <div style={{ marginLeft:"-150px" }}>
                        <ProSidebar >

                    <SidebarContent>
                        <Menu iconShape="square" icon={<BiMenu />} >
                            <MenuItem icon={<BiCommentEdit />}>Comments<Link to="/product_manager_comments" /></MenuItem>
                            <MenuItem icon={<BiShoppingBag />}>Products</MenuItem>
                            {/*  <SubMenu title="Components" >
                        <MenuItem>Component 1</MenuItem>
                        <MenuItem>Component 2</MenuItem>
                    </SubMenu>
                    */}
                        </Menu>
                    </SidebarContent>


                        </ProSidebar> </div>
                <Table responsive striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <td>Product</td>
                            <td>Id</td>
                            <td>User</td>
                            <td>Rating</td>
                            <td>Text</td>
                            <td>Validate</td>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>ayakkabi</td>
                            <td>0</td>
                            <td>gul</td>
                            <td>5</td>
                            <td>harika</td>
                            <td  ><Indeterminate /></td>
                        </tr>
                        {this.getInvalidComments}
                    </tbody>
                        </Table>
            
            </div>

        );
    }

}
export default product_manager_comments;
