import { ADD_TO_CART, REMOVE_ITEM, SUB_QUANTITY, ADD_QUANTITY, ADD_SHIPPING } from './action-types/cart-actions';
import tracker from '../src/api/tracker';
//add cart action
export const addToCart = async (quantity, price, id) => {
    try {
        const response = await tracker.put('/basket/:basketId', {
            quantity: quantity,
            price: price,
            product: id,

        });
        var basket = response.data.basket;
        console.log("basket: ", basket);
        return {
            type: ADD_TO_CART,
            id
        };
    } catch (e) {
        console.log(e.message);
    }
   
};
//remove item action
export const removeItem = async(id) => {
    try {
        const response = await tracker.put('/basket/delete/:basketId', {
            product: id,

        });
        var basket = response.data.basket;
        
        console.log("removed", basket);
        return {
            type: REMOVE_ITEM,
            id,
            

        };
    } catch (e) {
        console.log(e.message);
    }
};
//subtract qt action
export const subtractQuantity = async (id) => {
    try {
        const response = await tracker.put('/basket/delete/byOne/:basketId', {
            product: id,

        });
        var basket = response.data.basket;
        console.log("remove_one", basket);
        return {
            type: SUB_QUANTITY,
            id
        };
    } catch (e) {
        console.log(e.message);
    }
    
};
//add qt action
export const addQuantity = async (id) => {
    
    try {
        const response = await tracker.put('/basket/byOne /: basketId', {
            product: id,

        });
        var basket = response.data.basket;
        console.log("add_one", basket);
        return {
            type: ADD_QUANTITY,
            id
        };
    } catch (e) {
        console.log(e.message);
    }

   
};
