import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import { BsBoxArrowInRight } from 'react-icons/bs';
import tracker from '../src/api/tracker';
import basket from './basket';
class sm_invoices extends Component {
    constructor() {
        super()
        this.state = {
            orders: [],
            total_price:0
        }
    }

    componentDidMount = async () => {
    
        try {
            const response = await tracker.get("order/all");
            const orderarray = response.data;
            this.setState({ orders: orderarray });
            console.log("state", this.state.orders);


        } catch (e) {
            console.log(e);
        }

    }
    getTotalPrice = async (basketid) => {
        const res = await tracker.get('/basket/' + basketid);
      
        console.log(res);
        const totalprice = res.data.productList.totalprice;
        await this.setState({ total_price: totalprice });
        return totalprice;
    }

    handleOrders = () => {
        try {
           
            if (this.state.orders.length !== 0) {
                var invoices = [];

                console.log(this.state.orders);
                var item;
                for (var i = 0; i < this.state.orders.length; i++) {
                    console.log(this.state.orders[i]);
                    const email = this.state.orders[i].email;
                    const status = this.state.orders[i].status;
                    const userid = this.state.orders[i].user;
                    const basketid = this.state.orders[i].basketid;
                    const address = this.state.orders[i].address;
                    const id = this.state.orders[i]._id;
                    this.getTotalPrice(basketid);

                    console.log(id);
                    item = (
                        <tr>
                            <td>{id}</td>
                            <td>06.01.2021</td>
                            <td>{email}</td>
                            <td>{userid}</td>
                            <td>{this.state.total_price}</td>
                            <td>{status}</td>
                            <td><BsBoxArrowInRight onClick={() => {
                                this.props.history.push("/sm_invoice_details/" + id);
                            }}/></td>
                        </tr>

                    );
                    console.log(item);
                    invoices.push(item);
                }
        



                return invoices;
            }
        }
        catch (e) {
            console.log(e)
        }

    }
    status(e) {
        this.setState({ status: e.target.value });
        console.log(this.state.status);
    }

    render() {
        return (
            <div >
                <Table responsive striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <td>Invoice ID</td>
                            <td>Invoice Date</td>
                            <td>Email</td>
                            <td>User</td>
                            <td>Total Price</td>
                            <td>Status</td>
                            <td>Details</td>
                        </tr>
                    </thead>
                    <tbody>
                        {this.handleOrders()}
                    </tbody>
                </Table>

            </div >


        );
    }
}
export default sm_invoices;
